import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { UsuariosService } from '../usuarios.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-usuario-formulario',
  templateUrl: './usuario-formulario.component.html',
  styleUrls: ['./usuario-formulario.component.css']
})
export class UsuarioFormularioComponent {

  formulario!: FormGroup;

  constructor(private usuarioService: UsuariosService, private route: Router, private formBuilder: FormBuilder) {
    this.formulario = this.formBuilder.group({
      nome: ['', [Validators.required]],
      email: ['', [Validators.required]],
      senha: ['', [Validators.required]],
    });
  }

  ngOnInit() {

  }

  get nome() {
    return this.formulario.get('nome')!;
  }

  get email() {
    return this.formulario.get('email')!;
  }

  get senha() {
    return this.formulario.get('senha')!;
  }

  salvarUsuario() {

    if(this.formulario.invalid) {
      return;
    }

    this.usuarioService.salvarUsuario(this.formulario.value).subscribe((usuario) => {
      this.route.navigate(['usuarios/listar'])
    },

    (error) => {
      console.log(this.formulario.value);

      alert('Falha ao cadastrar usuario' + JSON.stringify(error));
    }

    )
  }

}
<link rel='stylesheet' href='//maxcdn.bootstrapcdn.com/bootstrap/3.3.4/css/bootstrap.min.css'/>
<div class="container">
  <form [formGroup]="formulario" (ngSubmit)="salvarUsuario()" #formDir="ngForm">
    <div class="form-row">
      <div class="form-group col-md-6">
        <label for="inputEmail4">Nome</label>
        <input formControlName="nome" type="text" class="form-control" placeholder="Nome">
        <div class="erro-validacao" *ngIf="nome.invalid && formDir.submitted">
          <p>Nome do cliente é obrigatório.</p>
        </div>
      </div>
      <div class="form-group col-md-6">
        <label for="inputPassword4">E-mail</label>
        <input formControlName="email" type="email" class="form-control" placeholder="E-mail">
        <div class="erro-validacao" *ngIf="email.invalid && formDir.submitted">
          <p>E-mail do cliente é obrigatório.</p>
        </div>
      </div>
      <div class="form-group col-md-6">
        <label for="inputPassword4">Senha</label>
        <input formControlName="senha" type="password" class="form-control" id="inputPassword4" placeholder="Senha">
        <div class="erro-validacao" *ngIf="senha.invalid && formDir.submitted">
          <p>Senha do cliente é obrigatório.</p>
        </div>
      </div>
    </div>
    <div class="form-row">

    </div>
    <div class="form-group">
      <div class="form-check">

      </div>
    </div>
    <button type="submit" class="btn btn-primary">Cadastrar</button>
  </form>
</div>
